import UseEffect from './components/UseEffect'
import UseState from './components/UseState'
import UseRef from './components/UseRef'
import Card from '@mui/material/Card';


function App() {

  return (
    <Card container
  spacing={0}
  direction="column"
  alignItems="center"
  justify="center"
  flexbox="center"
  style={{ minHeight: '100vh' }}>
    <h3>useState Hook: </h3>
    <UseState />
    <hr></hr>
    <h3>useEffect hook:</h3>
    <UseEffect />
    <hr></hr>
      <h3>useRef hook:</h3>
      <UseRef />
      <hr></hr>
    </Card>
  );
}

export default App;
